import './JobsComponent.css';

export default function JobsComponent(){


    const jobListings = [
        { role: "Intern Software Engineer", company: "Standard Bank", location: "Johannesburg" },
        { role: "Junior Web Developer", company: "ABSA", location: "Cape Town" },
    ];
    

    return(
        <div className="row gx-3">
        {jobListings.map((job, index) => (
            <div key={index} className="card mt-5 col-12 col-sm-12 col-md-6">
                <div className="card-body py-3">
                    <div className="d-flex justify-content-between">
                        <div className="col-12 col-sm-12 col-md-8">
                            <h5 className="jobs-role-name">{job.role}</h5>
                            <h6 className="jobs-company-name">
                                {job.company}, {job.location}
                            </h6>
                        </div>
                        <div className="d-flex align-items-center">
                            <button className="btn btn-sm px-5 py-3 text-white jobs-apply-btn">
                                APPLY
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        ))}
    </div>
    );
}
