import JobsComponent from './micro-components/JobsComponent';
import './OpportunitiesSection.css';

//create a seperate component for loading actual jobs

export default function OpportunitiesSection(){


    const jobListings = [
        {
          role: "Intern Software Engineer",
          company: "Standard Bank",
          location: "Johannesburg",
        },
        {
          role: "Intern UX | UI Designer",
          company: "Amazon",
          location: "Cape Town",
        },
        {
            role: "Intern UX | UI Designer",
            company: "Amazon",
            location: "Cape Town",
          },
      ];

    return(
        <div className="container-fluid opportunities-section-parent-wrapper py-5">
            <div className="container opportunities-section-child-wrapper py-3">
                <div className="opportunities-content-wrapper">
                    <h2 className="opportunities-main-heading text-white">
                        Available Roles
                    </h2>
                    <div className="row gx-3 gy-2  jobs-component-row-wrapper">
          {jobListings.map((job, index) => (
            <div key={index} className="card  col-12 col-sm-12 col-md-6">
              <div className="card-body">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <h5 className="jobs-role-name">{job.role}</h5>
                    <h6 className="jobs-company-name">
                      {job.company} ,  {job.location}
                    </h6>
                  </div>
                  <div className="d-flex align-items-center">
                    <button className="btn btn-sm px-5 py-3 text-white jobs-apply-btn">
                      APPLY
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
                </div>
            </div>
        </div>
    );
}
